import React, { createContext, useContext, useReducer } from 'react';
import { ShoppingCart, Leaf, Plus, Minus, Trash2, ArrowLeft } from 'lucide-react';

// Redux-like Store Setup
const CartContext = createContext();

const cartReducer = (state, action) => {
  switch (action.type) {
    case 'ADD_TO_CART':
      return {
        ...state,
        items: [...state.items, { ...action.payload, quantity: 1 }]
      };
    case 'REMOVE_FROM_CART':
      return {
        ...state,
        items: state.items.filter(item => item.id !== action.payload)
      };
    case 'INCREMENT_QUANTITY':
      return {
        ...state,
        items: state.items.map(item =>
            item.id === action.payload
                ? { ...item, quantity: item.quantity + 1 }
                : item
        )
      };
    case 'DECREMENT_QUANTITY':
      return {
        ...state,
        items: state.items.map(item =>
            item.id === action.payload && item.quantity > 1
                ? { ...item, quantity: item.quantity - 1 }
                : item
        )
      };
    default:
      return state;
  }
};

const CartProvider = ({ children }) => {
  const [state, dispatch] = useReducer(cartReducer, { items: [] });
  return (
      <CartContext.Provider value={{ state, dispatch }}>
        {children}
      </CartContext.Provider>
  );
};

const useCart = () => {
  const context = useContext(CartContext);
  if (!context) throw new Error('useCart must be used within CartProvider');
  return context;
};

// Plant Data
const plants = [
  { id: 1, name: 'Aloe Vera', price: 12.99, category: 'Succulents', image: 'https://images.unsplash.com/photo-1509587584298-0f3b3a3a1797?w=400' },
  { id: 2, name: 'Jade Plant', price: 15.99, category: 'Succulents', image: 'https://images.unsplash.com/photo-1459156212016-c812468e2115?w=400' },
  { id: 3, name: 'Snake Plant', price: 18.99, category: 'Air Purifying', image: 'https://images.unsplash.com/photo-1593482892290-f54927ae1bb8?w=400' },
  { id: 4, name: 'Peace Lily', price: 22.99, category: 'Air Purifying', image: 'https://images.unsplash.com/photo-1593691509543-c55fb32d8de5?w=400' },
  { id: 5, name: 'Monstera', price: 35.99, category: 'Tropical', image: 'https://images.unsplash.com/photo-1614594975525-e45190c55d0b?w=400' },
  { id: 6, name: 'Fiddle Leaf Fig', price: 45.99, category: 'Tropical', image: 'https://images.unsplash.com/photo-1597305877032-0668b3c6413a?w=400' },
  { id: 7, name: 'Basil', price: 8.99, category: 'Herbs', image: 'https://images.unsplash.com/photo-1618375569909-3c8616cf7733?w=400' },
  { id: 8, name: 'Mint', price: 7.99, category: 'Herbs', image: 'https://images.unsplash.com/photo-1628556270448-4d4e4148e1b1?w=400' }
];

// Header Component
const Header = ({ currentPage, onNavigate }) => {
  const { state } = useCart();
  const totalItems = state.items.reduce((sum, item) => sum + item.quantity, 0);

  return (
      <header className="bg-green-700 text-white shadow-lg sticky top-0 z-50">
        <div className="container mx-auto px-6 py-4">
          <div className="flex justify-between items-center">
            <div className="flex items-center gap-3 cursor-pointer hover:opacity-80 transition-opacity" onClick={() => onNavigate('landing')}>
              <Leaf className="w-8 h-8" />
              <h1 className="text-2xl font-bold">Paradise Nursery</h1>
            </div>
            <nav className="flex items-center gap-6">
              {currentPage !== 'products' && (
                  <button
                      onClick={() => onNavigate('products')}
                      className="hover:text-green-200 transition-colors font-medium text-lg"
                  >
                    Shop Plants
                  </button>
              )}
              {currentPage !== 'cart' && (
                  <button
                      onClick={() => onNavigate('cart')}
                      className="relative hover:text-green-200 transition-colors"
                  >
                    <ShoppingCart className="w-7 h-7" />
                    {totalItems > 0 && (
                        <span className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full w-6 h-6 flex items-center justify-center text-xs font-bold">
                    {totalItems}
                  </span>
                    )}
                  </button>
              )}
            </nav>
          </div>
        </div>
      </header>
  );
};

// Landing Page
const LandingPage = ({ onNavigate }) => {
  return (
      <div className="min-h-screen bg-gray-900">
        <div
            className="relative min-h-screen bg-cover bg-center flex items-center justify-center"
            style={{
              backgroundImage: 'url(https://images.unsplash.com/photo-1466781783364-36c955e42a7f?w=1600)',
            }}
        >
          <div className="absolute inset-0 bg-black opacity-50"></div>
          <div className="relative z-10 text-center text-white px-6 py-12 max-w-4xl mx-auto">
            <div className="flex items-center justify-center gap-4 mb-8">
              <Leaf className="w-20 h-20" />
              <h1 className="text-6xl md:text-7xl font-bold">Paradise Nursery</h1>
            </div>
            <p className="text-xl md:text-2xl mb-10 leading-relaxed">
              Welcome to Paradise Nursery, where green meets serenity. We are passionate about bringing nature closer to you. Our extensive collection of houseplants is carefully curated to enhance your living spaces with beauty, freshness, and tranquility. Whether you're a seasoned plant enthusiast or just beginning your green journey, we have the perfect plants to transform your home into a lush paradise.
            </p>
            <button
                onClick={() => onNavigate('products')}
                className="bg-green-600 hover:bg-green-700 text-white font-bold py-4 px-10 rounded-lg text-xl transition-all transform hover:scale-105 shadow-2xl"
            >
              Get Started
            </button>
          </div>
        </div>
      </div>
  );
};

// Product Listing Page
const ProductListingPage = ({ onNavigate }) => {
  const { state, dispatch } = useCart();
  const categories = [...new Set(plants.map(p => p.category))];

  const isInCart = (plantId) => {
    return state.items.some(item => item.id === plantId);
  };

  const handleAddToCart = (plant) => {
    dispatch({ type: 'ADD_TO_CART', payload: plant });
  };

  return (
      <div className="min-h-screen bg-gray-50">
        <Header currentPage="products" onNavigate={onNavigate} />
        <div className="container mx-auto px-6 py-12">
          <h2 className="text-5xl font-bold text-gray-800 mb-12 text-center">Our Collection</h2>
          {categories.map(category => (
              <div key={category} className="mb-16">
                <h3 className="text-3xl font-semibold text-green-700 mb-8 pb-3 border-b-4 border-green-300">
                  {category}
                </h3>
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
                  {plants.filter(p => p.category === category).map(plant => (
                      <div key={plant.id} className="bg-white rounded-xl shadow-lg overflow-hidden hover:shadow-2xl transition-all duration-300 transform hover:-translate-y-1">
                        <img
                            src={plant.image}
                            alt={plant.name}
                            className="w-full h-56 object-cover"
                        />
                        <div className="p-6">
                          <h4 className="text-xl font-bold text-gray-800 mb-3">{plant.name}</h4>
                          <p className="text-3xl font-bold text-green-600 mb-5">${plant.price}</p>
                          <button
                              onClick={() => handleAddToCart(plant)}
                              disabled={isInCart(plant.id)}
                              className={`w-full py-3 px-4 rounded-lg font-semibold text-lg transition-all ${
                                  isInCart(plant.id)
                                      ? 'bg-gray-300 text-gray-600 cursor-not-allowed'
                                      : 'bg-green-600 text-white hover:bg-green-700 shadow-md hover:shadow-lg'
                              }`}
                          >
                            {isInCart(plant.id) ? 'Added to Cart' : 'Add to Cart'}
                          </button>
                        </div>
                      </div>
                  ))}
                </div>
              </div>
          ))}
        </div>
      </div>
  );
};

// Shopping Cart Page
const ShoppingCartPage = ({ onNavigate }) => {
  const { state, dispatch } = useCart();

  const totalCost = state.items.reduce(
      (sum, item) => sum + item.price * item.quantity,
      0
  );

  const totalItems = state.items.reduce((sum, item) => sum + item.quantity, 0);

  const handleIncrement = (id) => {
    dispatch({ type: 'INCREMENT_QUANTITY', payload: id });
  };

  const handleDecrement = (id) => {
    dispatch({ type: 'DECREMENT_QUANTITY', payload: id });
  };

  const handleRemove = (id) => {
    dispatch({ type: 'REMOVE_FROM_CART', payload: id });
  };

  const handleCheckout = () => {
    alert('Coming Soon! Checkout functionality will be available soon.');
  };

  return (
      <div className="min-h-screen bg-gray-50">
        <Header currentPage="cart" onNavigate={onNavigate} />
        <div className="container mx-auto px-6 py-12">
          <h2 className="text-5xl font-bold text-gray-800 mb-10 text-center">Shopping Cart</h2>

          {state.items.length === 0 ? (
              <div className="text-center py-20">
                <ShoppingCart className="w-32 h-32 mx-auto text-gray-300 mb-6" />
                <p className="text-3xl text-gray-500 mb-8">Your cart is empty</p>
                <button
                    onClick={() => onNavigate('products')}
                    className="bg-green-600 hover:bg-green-700 text-white font-bold py-4 px-8 rounded-lg text-lg transition-all shadow-lg hover:shadow-xl"
                >
                  Continue Shopping
                </button>
              </div>
          ) : (
              <div className="grid grid-cols-1 lg:grid-cols-3 gap-10">
                <div className="lg:col-span-2">
                  <div className="bg-white rounded-xl shadow-lg p-8">
                    {state.items.map(item => (
                        <div key={item.id} className="flex flex-wrap items-center gap-6 py-6 border-b last:border-b-0">
                          <img
                              src={item.image}
                              alt={item.name}
                              className="w-28 h-28 object-cover rounded-lg shadow-md"
                          />
                          <div className="flex-1 min-w-40">
                            <h3 className="text-xl font-bold text-gray-800 mb-1">{item.name}</h3>
                            <p className="text-gray-600 text-lg">${item.price.toFixed(2)} each</p>
                          </div>
                          <div className="flex items-center gap-3">
                            <button
                                onClick={() => handleDecrement(item.id)}
                                className="bg-gray-200 hover:bg-gray-300 p-2 rounded-lg transition-colors"
                            >
                              <Minus className="w-5 h-5" />
                            </button>
                            <span className="w-14 text-center font-bold text-xl">{item.quantity}</span>
                            <button
                                onClick={() => handleIncrement(item.id)}
                                className="bg-green-600 hover:bg-green-700 text-white p-2 rounded-lg transition-colors"
                            >
                              <Plus className="w-5 h-5" />
                            </button>
                          </div>
                          <div className="text-right min-w-24">
                            <p className="text-2xl font-bold text-gray-800">
                              ${(item.price * item.quantity).toFixed(2)}
                            </p>
                          </div>
                          <button
                              onClick={() => handleRemove(item.id)}
                              className="text-red-500 hover:text-red-700 p-2 transition-colors hover:bg-red-50 rounded-lg"
                          >
                            <Trash2 className="w-6 h-6" />
                          </button>
                        </div>
                    ))}
                  </div>
                </div>

                <div className="lg:col-span-1">
                  <div className="bg-white rounded-xl shadow-lg p-8 sticky top-24">
                    <h3 className="text-3xl font-bold text-gray-800 mb-6">Order Summary</h3>
                    <div className="space-y-4 mb-8">
                      <div className="flex justify-between text-xl">
                        <span className="text-gray-600">Total Items:</span>
                        <span className="font-bold">{totalItems}</span>
                      </div>
                      <div className="flex justify-between text-3xl font-bold border-t-2 pt-5">
                        <span>Total:</span>
                        <span className="text-green-600">${totalCost.toFixed(2)}</span>
                      </div>
                    </div>
                    <button
                        onClick={handleCheckout}
                        className="w-full bg-green-600 hover:bg-green-700 text-white font-bold py-4 px-6 rounded-lg mb-4 transition-all text-lg shadow-md hover:shadow-lg"
                    >
                      Checkout
                    </button>
                    <button
                        onClick={() => onNavigate('products')}
                        className="w-full bg-gray-200 hover:bg-gray-300 text-gray-800 font-bold py-4 px-6 rounded-lg transition-all flex items-center justify-center gap-2 text-lg"
                    >
                      <ArrowLeft className="w-5 h-5" />
                      Continue Shopping
                    </button>
                  </div>
                </div>
              </div>
          )}
        </div>
      </div>
  );
};

// Main App Component
export default function App() {
  const [currentPage, setCurrentPage] = React.useState('landing');

  return (
      <CartProvider>
        {currentPage === 'landing' && <LandingPage onNavigate={setCurrentPage} />}
        {currentPage === 'products' && <ProductListingPage onNavigate={setCurrentPage} />}
        {currentPage === 'cart' && <ShoppingCartPage onNavigate={setCurrentPage} />}
      </CartProvider>
  );
}
